INSERT INTO species (latin_name, name, description, is_animal, discoverable_winter, discoverable_spring, discoverable_summer, discoverable_autumn) VALUES
    ('Pelophylax esculentus', 'Grøn frø', 'Grøn frø er en stor og kraftig frø med lange og muskuløse bagben, idet hannen kan opnå en kropslængde af ca. 5,5-9,5 cm, og hunnen kan blive 6,0-11,0 cm. Undtagelsesvis kan begge køn blive 0,5-1,0 cm længere. Øjnene sidder på hovedets overside, og er placeret ret tæt på hinanden, idet grøn frø er tilpasset et liv i vandet, hvor den kan ligge i vandoverfladen, så øjne og næsebor lige rager op over vandet. Snuden er relativ spids. Et ofte sikkert kendetegn er, at grundfarven på oversiden er tydeligt lysende grøn, men den kan undertiden også gå over i en mørk og mere dyster grøn farve. Det hænder også, at grundfarven er brun. På ryg og kropssider ses i vekslende omfang iøjnefaldende sorte pletter. Ned langs rygradden løber næsten altid en fin lys rygstribe.', 1, 1, 0, 0, 0),
    ('Apis mellifera', 'Honningbi', 'En mellemstor bi med hhv. arbejdere, dronninger og droner. Oftest ses arbejderne der alle er hunner. Arbejderne varierer en del i mængden af behåring på bagkroppen. Nogle individer er tydeligt båndede mens andre er næsten uden hår. Den underliggende farve kan også variere en del, fra lys brun til næsten sort. Dronninger minder om arbejderne, men er større og mere robuste, med en lang bagkrop, designet til æglægning. Dronninger mangler også den glatte yderside af bagerste skinneben (pollenkurv), som arbejderne har. Hannerne har heller ikke nogen pollenkurv, men har store øjne der mødes i toppen.', 1, 0, 1, 1, 0),
    ('Cyanistes caeruleus', 'Blåmejse', 'En lille fugl af mejsefamilien, som er lyst gul på undersiden og har blå vinger, hale, nakke og isse. Dens hvide kinder brydes desuden af en mørkeblå banditmaske, som går fra næb til nakke. Kønnene er stort set ens, selvom hunnen som regel er mere mat i farverne end hannen. Dette gælder især halen og de blå partier i nakken og oven på hovedet. Ungfuglene er matte i farverne og har gullige kinder. Længde cirka 10,5-12 cm. Adfærd, form og størrelse minder om andre mejsearters.', 1, 1, 1, 1 ,1),
    ('Cichorium intybus', 'Cikorie', 'Cikorie er en 30-100 cm. høj urt med iøjnefaldende lyseblå blomster. Stænglen er opret, kantet og hul, foroven grenet. Stængelbladene er lancetformede, fåtandede, med hjerteformet stængelomfattende basis, på oversiden glatte. Nedre blade omvendt lancetformede, fjersnitdelte eller tandede, på undersiden stivhårede på strengene. Kurvene er 3-5 cm. brede, med tungeformede, kønne lyseblå blomster. De ydre kurvblade er kirtelhårede. Frugten er 2-3 mm, kantet, uden fnok, men med enkelte små skæl. Blomsterkurvene åbnes fra tidlig morgen og følger solens gang. Den enkelte kurv visner hurtigt.', 0, 0, 0, 1, 0),
    ('Armeria maritima', 'Engelsk græs', 'En flerårig plante, der ved grunden har en roset af 5-10 cm. lange linjeformede, en smule sukkulente blade med but afrundet spids. Blomsterskafterne er bladløse og indtil 40 cm. høje og fra glatte til dunhårede-hårede. Blomsterstanden er hovedformet og omgivet af hindeagtige svøbblade, hvor de ydre har nedadrettede forlængelser, der omslutter blomsterskaftets øvre del - og vækstzonen, der befinder sig lige under blomsterstanden. Blomsterne er vellugtende og rosenrøde til hvide.',0, 0, 1, 1, 1),
    ('Somateria mollissima', 'Ederfugl', 'Ederfuglen er en af vores mest talrige havdykænder. Det er en stor og kraftig dykand, 60-70 cm lang og med et vingefang på ca. 1 meter. Den har et meget karakteristisk langt trekantet næb, der gør den nem at kende i silhuet. Hannen (adult) er utrolig flot i pragtdragt med hvid hals og ryg, mens flanker, mave og hale er sort. Hovedet er hvidt med sort isse og i kontrast er nakkefjerene olivengrønne. Eclipsedragten, som hannen har under fjerfældningen i juli-september, er mere brunlig/spættet med spredte hvide områder på kroppen. Det tager ca. 3 år før hannen har opnået sin pragtdragt. Indtil da er fjerdragten meget broget i sorte, hvide og brune nuancer. Hunnen er brunlig og med en lys næbspids. Juvenile hunner har svage aftegninger og lys øjenbrynsstribe.', 1, 1, 1, 1, 1),
    ('Tulipa sylvestris', 'Vild tulipan','Vild tulipan er naturligt udbredt i Middelhavsområdet med tyngdepunktet i Tyrkiet, Grækenland og på Sicilien. Senere er den spredt og naturaliseret fra dyrkning over store dele af Europa. Den findes voksende enkelte steder i Danmark, hvor den er forvildet fra have og park. Arten foretrækker lysåbne voksesteder i krat, langs skovbryn og hække og i vinmarker, hvor jorden er leret og kalkrig.', 0,0,1,1,0),
    ('Phoca vitulina', 'Spættet sæl', 'Den spættede sæl er den eneste sælart der med sikkerhed yngler i Danmark. Parringstid i juni-juli. Drægtighed 11 mdr. Føder en unge. Kønsmodne ved ca 130 cm, svarende til 3-5 år. Levetid op til 36 år. Den spættede sæl er ekstremt tilpasset et liv i vand. Den kan ikke, som f.eks. søløver, bruge lufferne på land, men må "mave" sig langsomt frem. Derfor holder den sig tæt på vandet og ses sjældent mere end et par meter fra kysten. I vandet kan den skyde en fart af op til 35 km/t, dykke til noget over 100 m og holde vejret i 20-30 min. Kommer oftest op efter luft med 5-10 min. intervaller. Spættede sæler lever i flokke af varierende størrelse uden hakkeorden eller en egentlig social struktur. Lever af fisk og krebsdyr.', 1, 1, 1, 1, 1);
